// ===== ARBITRARY VALUE SUPPORT =====

// Bg supports arbitrary color values with bracket notation
func Bg(color string) css.Rule {
	if color[0] == '#' || color[0] == 'r' { // #hex or rgb() values
		className := "bg-[" + color + "]"
		return defaultManager.GetOrCreateRule(className, func() css.Rule {
			return css.RuleSet("."+className,
				css.Set(css.BackgroundColor, css.Raw(color)),
			)
		})
	}
	// Use theme color
	return BackgroundColor(defaultManager, color)
}